import styles from './AboutMeShort.module.scss';
import portret from '../../../images/portret.jpg';

export default function AboutMeShort() {
  const history = useNavigate();
  const handleCklick = () => history('/blog');

  const [isVisible, setVisible] = useState(true);
  const domRef = useRef();
  useEffect(() => {
    const b = domRef;
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        console.log(entry.isIntersecting);
        //if (entry.isIntersecting) {
          setVisible(entry.isIntersecting)
        //} 
      });
    });
    observer.observe(domRef.current);
    return () => observer.unobserve(b);
  }, []);


  return (
    <div style={{
      margin: 'auto',
      padding: '50px 0px 50px 0px',
      width: '95%'
    }}>
      <img
        style={{
          display: 'inline-block',
          //imageOrientation: '90deg',
          width: '100%',
          maxWidth: '250px',
        }}
        //className={styles.aboutMeImage}
        className={`${styles.fadeInSection} ${isVisible ? styles.isVisible : ''}`}
        src={portret}
        alt='Iryna&apos;s portret'

        ref={domRef}
      />
      <div style={{
        display : 'inline-block',
        width: '50%',
        //width: '60%',
        minWidth: '300px',
        padding: 30,
        textAlign: 'left'
      }}>
        <h1 style={{
          color: '#b08c59'
        }}>Iryna Sheremeta</h1>
        <p>
          This is something about me and what I love to do when I want to do it
          So take a seat and relax if you have time. But I belive that you actuly
          have that beautiful and dreamfool time. So lets go to speak a bit.
          I&apos;me a photographer of a big field of bright life.
        </p>
        <MyButton
          className={styles.aboutMeButton}
          borderColor='#8E8D8A'
          name="About me"
          handleCklick={handleCklick}
        />
      </div>
      {/* <div className={`fade-in-section ${isVisible ? 'is-visible' : ''}`}> */}
      {/* <div 
        className={`${styles.fadeInSection} ${isVisible ? styles.isVisible : ''}`}
        ref={domRef}
      >
        <p>
          sdghsdgsdgsdgsdgsdgsdgsdgssdghsdgsdgsdgsdgsdgsdgsdgssdghsdgsdgsdgsdgsdgsdgsdgs
        </p>
      </div> */}
      {/* <button onClick={handleCl}>press me</button> */}
      {/* <p>{isVisible.toString()}</p> */}
    </div>
  )
}
